package java.project.lvl2.app;

import java.project.lvl2.list.LinkedList;

import static java.project.lvl2.utilities.StringUtils.join;
import static java.project.lvl2.utilities.StringUtils.split;
import static java.project.lvl2.app.MessageUtils.getMessage;

import org.apache.commons.text.WordUtils;

public class App {
    public static void main(String[] args) {
        LinkedList tokens;
        tokens = split(getMessage());
        String result = join(tokens);
        System.out.println(WordUtils.capitalize(result));
    }
}
